mapper.select:
  	    select * from rptpub t where 1=1

	  	    and t.toolflag=#{toolFlag, jdbcType=VARCHAR}


	  	    and t.tool_title like CONCAT('%', #{toolTitle,jdbcType=VARCHAR}, '%')

  	    select count(1) from rptpub t where 1=1

	  	    and t.toolflag=#{toolFlag, jdbcType=VARCHAR}


	  	    and t.tool_title like CONCAT('%', #{toolTitle,jdbcType=VARCHAR}, '%')
